import styled, {css} from 'styled-components';
import {Link} from 'react-router-dom';

// const OptionContainerStyles = css`
//     padding: 10px 15px;
//     cursor: pointer;  
// `;

export const HeaderContainer = styled.div`
    height: 70px;
    width: 100%;
    display: flex;
    justify-content: space-between;
    margin-top: 0;
    margin-bottom: 20px;

  ${'' /* position: fixed; */}

    @media screen and (max-width: 800px) {
          height:60px;
          padding: 10px;
          margin-bottom: 20px; 
    }
`;

export const ImageStyles = styled.img`
    height: 45px;
`;

export const LogoContainer = styled(Link)`
    height: 100%;

    padding: 10px;
    display: flex;
    justify-content: space-between;
    align-items: center;

    @media screen and (max-width: 800px) {
          width: 20px;
          padding: 0;
    } 
`;

export const NameContainer = styled.div`

    font-size: 30px;
    font-weight: 900;
    color: #FA663D;
    padding: 7px;
    @media screen and (max-width: 800px) {
          font-size: 12px;
          padding: 3px;
    } 

`;


export const OptionLink = styled(Link)`
     padding: 10px 15px;
     cursor: pointer; 
     font-weight: 900;
     font-size: 20px;
     color: #707b8f;

     &:hover{
         color: #ec407a;
     }

     @media screen and (max-width: 419px) {
        padding: 5px 10px;
        font-size: 10px;
     }

     @media screen and (max-width: 519px) and (min-width: 420px){
        padding: 6px 11px;
        font-size: 12px;
     }

     ${'' /* @media screen and (max-width: 420px) {
        padding: 6px 11px;
        font-size: 12px;
     }

     @media screen and (max-width: 520px) {
        padding: 7px 12px;
        font-size: 14px;
     }

     @media screen and (max-width: 620px) {
        padding: 8px 13px;
        font-size: 16px;
     }

     @media screen and (max-width: 720px) {
        padding: 9px 14px;
        font-size: 17px;
     }

     @media screen and (max-width: 820px) {
        padding: 10px 15px;
        font-size: 18px;
     }     */}
`;

export const OptionsContainer = styled.div`

    width: 50%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: flex-end; 

    @media screen and (max-width: 800px) {
          width: 80%;
    } 

`;

